import React, { Component } from "react";
import { withAuth0 } from "@auth0/auth0-react";

import Logo from "../../assets/logo/mm-shield.png";
import sidenavAvi from "../../assets/icons/sidenav-avatar.png";
import dashboardIcon from "../../assets/icons/home.png";
import upcomingWorkoutsIcon from "../../assets/icons/upcoming-workout.png";
import bookWorkoutIcon from "../../assets/icons/book-workout-menu.png";
import videoIcon from "../../assets/icons/video-library-menu.png";
import documentsIcon from "../../assets/icons/documents-menu.png";
// import BackToTop from "./back-to-top";
class Nav extends Component {
  state = {
    sideNavOpen: false,
    // contentBrightness: 100,
  };

  openNav = () => {
    document.getElementById("sideNav").style.width = "270px";
    document.getElementById("slideContentRight").style.marginLeft = "270px";
    // const opacity = document.getElementsByClassName("dashOpacity");
    // const newOpacityArray = Array.from(opacity);
    // newOpacityArray.forEach((element) => {
    //   element.style.filter = "brightness(50%)";
    // });
    this.setState({ sideNavOpen: true });
  };

  closeNav = () => {
    document.getElementById("sideNav").style.width = "0";
    document.getElementById("slideContentRight").style.marginLeft = "0";
    this.setState({ sideNavOpen: false });
  };

  logoutWithRedirect = () =>
    this.props.auth0.logout({
      returnTo: window.location.origin,
    });

  render() {
    const { user } = this.props.auth0;
    // const baseOverlayClasses = "overlayBase"
    const overlayClasses =
      "overlayBase fill " +
      (this.state.sideNavOpen ? "overlay" : "overlay-hide");
    return (
      <div id="dashboardContainer">
        <div className={overlayClasses} onClick={this.closeNav} />
        <div id="slideContentRight">
          <nav
            id="navbar"
            className="navbar navbar-dark dashOpacity"
            style={{ backgroundColor: "#E41A37" }}
          >
            <div
              style={{ width: "200px" }}
              className="container mx-auto position-absolute top-50 start-50 translate-middle"
            >
              <a style={{ width: "100%" }} className="navbar-brand" href="">
                <img
                  id="navbarLogo"
                  src={Logo}
                  alt="Mychal Martinez Basketball Logo"
                  width="50"
                  height="50"
                />
              </a>
            </div>
            <div className="position-absolute top-50 start-0 translate-middle-y ms-3">
              <span id="hamburgerBtn" onClick={this.openNav}>
                &#9776;
              </span>
            </div>
          </nav>
          <div id="sideNav" className="sidenav">
            <div>
              <a
                href="javascript:void(0)"
                className="closebtn"
                onClick={this.closeNav}
              >
                &times;
              </a>
            </div>
            <div id="sidenaveProfile" className="sidenavProfile">
              <img src={user?.picture} alt="" />
              <p id="sidenaveProfUser">{user?.name}</p>
            </div>
            <div>
              <ul className="">
                <li className="">
                  <img
                    className=""
                    src={dashboardIcon}
                    alt=""
                    height="30"
                    width="30"
                    style={{ display: "inline-block" }}
                  />
                  <a
                    className=""
                    href="./dashboard.html"
                    style={{ display: "inline-block" }}
                  >
                    Dashboard
                  </a>
                </li>
                <li className="">
                  <img
                    className=""
                    src={upcomingWorkoutsIcon}
                    alt=""
                    height="30"
                    width="30"
                    style={{ display: "inline-block" }}
                  />
                  <a
                    className=""
                    href="./upcoming-workouts.html"
                    style={{ display: "inline-block" }}
                  >
                    Upcoming Workouts
                  </a>
                </li>
                <li className="">
                  <img
                    className=""
                    src={bookWorkoutIcon}
                    alt=""
                    height="30"
                    width="30"
                    style={{ display: "inline-block" }}
                  />
                  <a
                    className=""
                    href="./book-workout.html"
                    style={{ display: "inline-block" }}
                  >
                    Book a Workout
                  </a>
                </li>
                <li className="">
                  <img
                    className=""
                    src={videoIcon}
                    alt=""
                    height="30"
                    width="30"
                    style={{ display: "inline-block" }}
                  />
                  <a
                    className=""
                    href="./video-library.html"
                    style={{ display: "inline-block" }}
                  >
                    Video Library
                  </a>
                </li>
                <li className="">
                  <img
                    className=""
                    src={documentsIcon}
                    alt=""
                    height="30"
                    width="30"
                    style={{ display: "inline-block" }}
                  />
                  <a
                    className=""
                    href="./documents.html"
                    style={{ display: "inline-block" }}
                  >
                    Documents
                  </a>
                </li>
                <li className="" style={{ marginTop: "20px" }}>
                  <a
                    className=""
                    href=""
                    data-bs-toggle="modal"
                    data-bs-target="#Modal"
                    data-bs-title="Send a Message"
                    // onClick={sendMessage}
                  >
                    Send Message
                  </a>
                </li>
                <li className="">
                  <a
                    className=""
                    href=""
                    data-bs-toggle="modal"
                    data-bs-target="#Modal"
                    data-bs-title="Account Settings"
                    // onClick={accountSetting}
                  >
                    Account Settings
                  </a>
                </li>
                <li className="" id="qsLogoutBtn" onClick={logoutWithRedirect}>
                  <a className="" onClick={logoutWithRedirect} href="#">
                    Logout
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default withAuth0(Nav);
