import React from "react";
import { Router, Route, Switch } from "react-router-dom";
import Nav from "./components/modules/navigation";
import { Container } from "reactstrap";
import { useAuth0 } from "@auth0/auth0-react";
import Loading from "./components/modules/loading.js";
import history from "./utils/history";
import initFontAwesome from "./utils/initFontAwesome";
import Dashboard from "./components/dashboard";
import UpcomingWorkouts from "./components/upcoming-workouts";

import "./App.css";

initFontAwesome();

const App = () => {
  const { isLoading, error, isAuthenticated, loginWithRedirect } = useAuth0();

  if (error) {
    return <div>Oops... {error.message}</div>;
  }

  if (isLoading) {
    return <Loading />;
  }

  if (!isAuthenticated) {
    loginWithRedirect();
  }

  return (
    <Router history={history}>
      {isAuthenticated && (
        <div id="app" className="d-flex flex-column h-100">
          <Nav />
          <Container className="flex-grow-1 mt-1">
            <Switch>
              <Route exact path="/" component={Dashboard} />
              <Route
                exact
                path="/upcoming-workouts"
                component={UpcomingWorkouts}
              />
              <Route
                exact
                path="/upcoming-workouts"
                component={UpcomingWorkouts}
              />
            </Switch>
          </Container>
        </div>
      )}
    </Router>
  );
};

export default App;
