import React, { useEffect, useState } from "react";
import { flattenDeep, groupBy } from "lodash";
import RecentlyAdded from "../components/modules/recently-added";
import videoIcon from "../assets/icons/video-library-dash.png";
import mockData from "../data.json";

const playlistIds = [
  "PLtAzkECGU1yXDNefSuSbxODu9hrRK0UtP",
  "PLtAzkECGU1yXhKBF1t-pJmH8S1nuBiu8z",
];

const VideoLibrary = ({ isHomepage }) => {
  const [playlists, setPlaylists] = useState([]);
  const [videos, setVideos] = useState([]);
  const [videosCopy, setVideosCopy] = useState([]);
  useEffect(() => {
    (async () => {
      try {
        const playlistRes = await fetch(
          "https://www.googleapis.com/youtube/v3/playlists?" +
            new URLSearchParams({
              key: "AIzaSyCAH8cKrD6ASDhtfMYIfEGzNU6v9mcltRI",
              part: "id,snippet",
              id: playlistIds.join(","),
            })
        );
        const playlistResJsOn = await playlistRes.json();
        setPlaylists(
          playlistResJsOn.items.map(({ id, snippet }) => ({
            id,
            title: snippet.localized.title,
          }))
        );

        const playlistItemsResJsOn = await Promise.all(
          playlistIds.map(async (playlistId) => {
            const playlistItemsRes = await fetch(
              "https://www.googleapis.com/youtube/v3/playlistItems?" +
                new URLSearchParams({
                  part: "snippet",
                  key: "AIzaSyCAH8cKrD6ASDhtfMYIfEGzNU6v9mcltRI",
                  channelId: "UCHBDAkjxufCaEfmRaf7Bj3Q",
                  maxResults: 200,
                  playlistId,
                })
            );
            return playlistItemsRes.json();
          })
        );
        let filteredVideos = flattenDeep(
          playlistItemsResJsOn.map((playlistItems) =>
            playlistItems.items.map((i) => i.snippet)
          )
        ).sort((a, b) => (a.publishedAt > b.publishedAt ? 1 : -1));
        filteredVideos = isHomepage
          ? filteredVideos.slice(0, 6)
          : filteredVideos;
        setVideos(filteredVideos);
        setVideosCopy(filteredVideos);
        //   setPlaylists(mockData.playlists);
        //   setVideos(mockData.videos);
        //   setVideosCopy(mockData.videos);
      } catch (err) {
        console.log("err==>>", err);
      }
    })();
  }, []);

  const filterVideos = (e) => {
    const { value } = e.target;
    if (value) {
      setVideos(videosCopy.filter((v) => v.playlistId === value));
    } else {
      setVideos(videosCopy);
    }
  };

  return (
    <div>
      <div class="container dashOpacity mb-5">
        <div id="video-wrapper" class="card mb-1">
          {!isHomepage && (
            <div className="">
              <select onChange={filterVideos}>
                <option value="">All Categories</option>
                {playlists.map((p, i) => (
                  <option key={i} value={p.id}>
                    {p.title}
                  </option>
                ))}
              </select>
            </div>
          )}

          <div class="mt-4">
            <img id="rec-videos-icon" src={videoIcon} alt="" />
            <h4 class="card-title fs-3">
              {isHomepage ? "Recently Added Videos" : "All Videos"}
            </h4>
            {/* <h6 class="card-subtitle mb-3 text-black-50">
              Updated on March 2nd
            </h6> */}
          </div>

          <div class="video-container">
            {videos.map((video, i) => (
              <div key={i} className="video">
                <iframe
                  width="100%"
                  height="280"
                  src={`https://www.youtube.com/embed/${video.resourceId.videoId}`}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                ></iframe>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default VideoLibrary;
