const router = require("express").Router();
const db = require("../models");

router.get("/video", (req, res) => {
  db.Video.find({
    title: ""
  })
    .then(video => res.json(video))
    .catch(err => res.status(422).end());
});
router.get("/pdf", (req, res) => {
  db.Pdf.find({
    title: ""
  })
    .then(pdf => res.json(pdf))
    .catch(err => res.status(422).end());
});

module.exports = router;
