import React from "react";

const VideoFilterModal = ({ handleClose, show, children }) => {
  const showHideClassName = show
    ? "videoFilterModal video-filter-modal-show-block"
    : "videoFilterModal video-filter-modal-show-none";

  return (
    <div className={showHideClassName}>
      <section className="video-modal-card card">
        {children}
        <div className="card-body">
          <button className='btn btn-danger' type="button" onClick={handleClose}>
            Close
          </button>
        </div>
      </section>
    </div>
  );
};

export default VideoFilterModal;
